<?php session_start();	include '../config/config.php';		$sql_min = mysql_query("select var_value from settings where var_name = 'reminder_minutes'") or die(mysql_error());	$sql_min_row = mysql_fetch_assoc($sql_min);		$remindermins = $sql_min_row['var_value'];		if($remindermins>0){				$hour = $_POST['hour'];		$minutes = $_POST['minutes'];		$curtime = $hour.':'.$minutes.':00';				$a=explode(':',$curtime);		$t1=$a[0]*3600+$a[1]*60+$a[2];				$a=explode(':','00:'.$remindermins.':00');		$t2=$a[0]*3600+$a[1]*60+$a[2];				$t3=$t1+$t2;				$h=floor($t3/3600);		$m=floor(($t3%3600)/60);		$s=$t3-$h*3600-$m*60;				$curtime =  $h.':'.$m;				//echo $curtime;				//add zero for minz 0-9		$dummytime = explode(':',$curtime);		$mm = $dummytime[1];		if($mm<10){			$mmmm = '0'.$mm;			}else{			$mmmm = $mm;		}		$curtime = $dummytime[0].':'.$mmmm;				//echo $curtime;				$sql = mysql_query("select DATE_FORMAT(r.asap_datetime,'%H:%i') as asaptime,DATE_FORMAT(r.time,'%H:%i') as time, r.id as rid from reservation_type as rt, account as a, reservation as r where rt.id=r.reservation_type_id and a.id=r.account_id and r.deleted=0 and r.acknowledged = 1 and rt.id in (2,3) and r.processed=0 and a.deleted=0 order by r.viewed asc, r.time asc");				$alarmids = array();				while($row = mysql_fetch_assoc($sql)){						$asaptime = $row['asaptime'];			$time = $row['time'];			$resid = $row['rid'];						if(''.$asaptime==''.$curtime || ''.$time==''.$curtime){			   array_push($alarmids,$resid);			}		}		if(count($alarmids)>0){			echo '00,'.implode(',',$alarmids);		}	}mysql_close($con);	?>